//The below code was OCR'd from the SuiteScript 2.0 book, may contain character errors

function getAllResultsPaged(searchId) {

/* {N/search.Search} */
var searchObj = search.load({id: searchId});

/* {N/search.PagedData} */
var pageData = searchObj.runPaged({pageSize: 1000});

log.debug({title: "Number Of Results", details: pageData.count});

var numPages = pageData.pageRanges.length;

var allResults = [];

var i = 0;

while (i < numPages)  {

allResults = allResults.concat (pageData.fetch(i).data);
i++;
}
return allResults;
}

