//time Unix epoch
  function time() {
      const now = new Date()  
      const secondsSinceEpoch = Math.round(now.getTime() / 1000)
      return secondsSinceEpoch;
  }

  //separate by commas amount

  function SepararComas(Number){
        try {
            if(Number=='' || !Number){
                return Number
            }
            var NumberNonString = '';
            var ArrayNumberString = [];
            var Size ;
            var Index;
            var Count = 0;
            var Pennies  = ''; 
            var CountTemp =0;
            var Amount = '';
            var Total = '';

          if(typeof Number == "string"){
            ArrayNumberString = Number.split('');
          }else{
            NumberNonString = String(Number)
            ArrayNumberString = NumberNonString.split('');
          };
          Size = ArrayNumberString.length;  
              if(Size){
                for(var i=0 ; i<Size;i++){
                    if(ArrayNumberString[i] === '.') {
                        Index = i
                    }else if(!Index){
                        Count ++
                    }
                    if(Index){
                        Pennies = Pennies + ArrayNumberString[i]
                    }
                 }
              }

            if(Count>3){

                for(var j=(Count -1) ; j>=0;j--){
                    if( CountTemp == 3) {
                        Amount = ',' + Amount 
                        CountTemp = 0
                    }
                    Amount = ArrayNumberString[j] + Amount 
                    CountTemp ++
                }
            }else{
                return Number
            }

          var Total = Amount + Pennies
            return Total
        } catch (error) {
            log.error({title:'>>error SepararComas', details:error});
            return false
        }
    }

