function(currentRecord, record, message, log) {
    
    /**
     * Function to be executed after page is initialized.
     * @param {Object} scriptContext
     * @param {Record} scriptContext.currentRecord - Current form record
     * @param {string} scriptContext.mode - The mode in which the record is being accessed (create, copy, or edit)
     *
     * @since 2015.2
     */
    function pageInit(scriptContext) {
        var inv_rec = scriptContext.currentRecord;
        var location = inv_rec.getValue('location');
        var rec_loc = record.load({
            type : record.Type.LOCATION,
            id : location
        });
        // 1 = CDMX    2 = QRO
        window.imp_local = rec_loc.getValue('custrecord_fe_impuesto_local');
        log.audit({title: 'ESTA FACTURA TIENE IMPUESTO PARA', details:window.imp_local});






    }

    /**
     * Function to be executed when field is changed.
     *
     * @param {Object} scriptContext
     * @param {Record} scriptContext.currentRecord - Current form record
     * @param {string} scriptContext.sublistId - Sublist name
     * @param {string} scriptContext.fieldId - Field name
     * @param {number} scriptContext.lineNum - Line number. Will be undefined if not a sublist or matrix field
     * @param {number} scriptContext.columnNum - Line number. Will be undefined if not a matrix field
     *
     * @since 2015.2
     */
    function fieldChanged(scriptContext) {

        //Si hay impuesto para esta localidad
        if(window.imp_local){
            var sublistName = scriptContext.sublistId;
            var sublistFieldName = scriptContext.fieldId;
            var line = scriptContext.line;
            if (sublistName === 'item' && sublistFieldName === 'item'){
                //Tomar el id del item de esta linea
                var inv_rec = scriptContext.currentRecord;
                var item_id = inv_rec.getCurrentSublistValue({sublistId: 'item',fieldId: 'item'});
                log.audit({title: 'EL ID de este item es ', details:item_id});
                //alert('EL ID de este item es: ' + item_id);
                //Cargar el registro de ese item
                var rec_item = record.load({
                    type : record.Type.INVENTORY_ITEM,
                    id : item_id
                });
                //Recuperar el valor del codigo de impuesto, ya sea Queretaro a CDMX
                //Si es CDMX
                if(window.imp_local == 1){
                    var cod_imp = rec_item.getValue('custitem_efx_grupo_local_cdmx');
                    log.audit({title: 'El codigo de impuesto es de la ciudad de mexico y para este item es el ', details:cod_imp});
                }
                //Si es QRO
                if(window.imp_local == 2){
                    var cod_imp = rec_item.getValue('custitem_efx_grupo_local_qro');
                    log.audit({title: 'El codigo de impuesto es de QRO y para este item es el ', details:cod_imp});
                }

                //Seleccionar la linea de la sublista del item en la factura
                var lineNum = inv_rec.selectLine({
                    sublistId: 'item',
                    line: line
                });

                inv_rec.setCurrentSublistValue({
                    sublistId: 'item',
                    fieldId: 'taxcode',
                    value: cod_imp,
                    ignoreFieldChange: true
                });

                // inv_rec.commitLine({
                //     sublistId: 'item'
                // });

            }

        }
    }

    /**
     * Function to be executed when field is slaved.
     *
     * @param {Object} scriptContext
     * @param {Record} scriptContext.currentRecord - Current form record
     * @param {string} scriptContext.sublistId - Sublist name
     * @param {string} scriptContext.fieldId - Field name
     *
     * @since 2015.2
     */
    function postSourcing(scriptContext) {



    }



    return {
        pageInit: pageInit,
        fieldChanged: fieldChanged
        //,
        //postSourcing: postSourcing
    };
    
});

