     * @property {string} type - Record type id
     *
     * @return {Array|Object|Search|RecordRef} inputSummary
     * @since 2015.1
     */
    function getInputData() {

        try{

            var busqueda_fact_col_a = search.create({
                type: search.Type.INVOICE,
                filters: [
                    ['custbody_efx_cons_autofac', search.Operator.ISEMPTY, '']
                    , 'AND',
                    ['mainline', search.Operator.IS, 'F']
                    , 'and',
                    ['item', search.Operator.ANYOF, '589']
                ], columns:
                [
                    search.createColumn({name: 'internalid', label: 'Internal ID'})
                ]
            });

            var searchResultCount = busqueda_fact_col_a.runPaged().count;
            log.debug("Result count: " + searchResultCount);

            return busqueda_fact_col_a

        }catch (e) {
            log.error({title:'getInputData - error',details:e});
        }


    }

    /**
     * Executes when the map entry point is triggered and applies to each key/value pair.
     *
     * @param {MapSummary} context - Data collection containing the key/value pairs to process through the map stage
     * @since 2015.1
     */
    function map(context) {

    }

    /**
     * Executes when the reduce entry point is triggered and applies to each group.
     *
     * @param {ReduceSummary} context - Data collection containing the groups to process through the reduce stage
     * @since 2015.1
     */
    function reduce(context) {

    }


    /**
     * Executes when the summarize entry point is triggered and applies to the result set.
     *
     * @param {Summary} summary - Holds statistics regarding the execution of a map/reduce script
     * @since 2015.1
     */
    function summarize(summary) {

    }

    return {
        getInputData: getInputData,
        map: map,
        reduce: reduce,
        summarize: summarize
    };
    
});

