function generateDigest(string) {


    return  CryptoJS.enc.Base64.stringify(CryptoJS.HmacSHA256(string, "yipnQ8lpewEBRA1FYbf7kHnpBykI3hGiHKWHRW0fjVnTSE94jF3UhYC"));

}
    var nounce = nonce(6);
    var ts = time();
    var string = build(nounce,ts);
    nlapiLogExecution('DEBUG', 'nounce', nounce);
    nlapiLogExecution('DEBUG', 'ts', ts);
    nlapiLogExecution('DEBUG', 'string', string.string);

var digest = generateDigest(string.string);
var headers= {
    Authorization: 'Hawk id="jNydJyPXcfGgst3kQNyM", ts="'+string.time+'", nonce="'+string.nounce+'", mac="'+digest+'"'
  };
  nlapiLogExecution('DEBUG', 'headers', JSON.stringify(headers));
  var response1 = nlapiRequestURL('https://app.fracttal.com/api/accounts/', 'null',headers,'GET') 
nlapiLogExecution('DEBUG', 'Digest', digest);
nlapiLogExecution('DEBUG', 'response1', JSON.stringify(response1) );
function nonce(length) {
    var result           = '';
    var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz1234567890_';
    var charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
}

function time() {
    const now = new Date()  
    const secondsSinceEpoch = Math.round(now.getTime() / 1000)
    return secondsSinceEpoch;
}
//'Hawk id="jNydJyPXcfGgst3kQNyM", ts="1671260800", nonce="53fAE7", mac="gk4L9v5Ch7gkm1Zn+BMASnbngooY/UsOhqUFYixaavY="'
function build(nounce,ts) {
    var time = ts.toString();
    var string =  'hawk.1.header\n'+time+'\n'+nounce+'\nGET\n/api/accounts/\napp.fracttal.com\n443\n\n\n'
    //string +=''
   var stringutf8 =  decodeURIComponent(string);
   //var stringutf8 = unescape(string);

   nlapiLogExecution('DEBUG', 'stringutf8', stringutf8);
    return {
        string:stringutf8,
        nounce:nounce,
        time:time
    } 

}
